import React, { useEffect, useState } from "react";
import "./style.css";

const FetchApi = (id) => {
    const [data, setData] = useState(null);

    const url = `https://jsonplaceholder.typicode.com/posts/${id}`;

    useEffect(() => {
        fetch(url)
        .then((response) => response.json())
        .then((json) => setData(json));
    }, [url]);
    
    return data;
};


